
public class BucleIF2 {


	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=17;
		
		//OPERADOR LÓGICO AND
		
		if(num>=10 && num<15) {
			System.out.println("El número es mayor o igual que 10 y menor que 15");
		}else {
			System.out.println("El numero es menor que 10");
		}
		
		//OPERADOR LÓGICO OR
		
		if(num>=10 || num<15) {
			System.out.println("El número es mayor o igual que 10 o menor que 15");
		}else {
			System.out.println("El numero es menor que 10");
		}
	}

}
